import {HttpClient} from '@angular/common/http';
import {Component} from '@angular/core';
import {FormGroup, FormControl, Validators} from '@angular/forms';
import {Router} from '@angular/router';

@Component({
    selector: 'login',
    templateUrl: './login.component.html',
})


export class LoginComponent {
    public static readonly MIN_CHAR_LENGTH = 5;
    IsAuthenticatiionFailed: boolean;

    loginForm = new FormGroup({
        UserName: new FormControl('', [
            Validators.required,
            Validators.minLength(LoginComponent.MIN_CHAR_LENGTH),
        ]),
        Password: new FormControl('', [
            Validators.required,
            Validators.minLength(LoginComponent.MIN_CHAR_LENGTH),
        ]),
    });

    constructor(private router: Router, private httpClient: HttpClient) {
        this.IsAuthenticatiionFailed = false;
    }

    login() {
        this.httpClient.post("http://localhost:4000/signin", this.loginForm.value).subscribe(response => {
            let dataJson = JSON.parse(JSON.stringify(response));
            if (dataJson.token == undefined) {
                this.IsAuthenticatiionFailed = true;
            } else {
                this.router.navigate(['customer']);
                localStorage['token'] = dataJson.token;
            }
        });
    }

    FailsRequired(inputName: string): boolean {
        let f = this.loginForm.get(inputName);
        return !(f?.dirty && f.errors?.['required']);
    }

    FailsMinChar(inputName: string): boolean {
        let f = this.loginForm.get(inputName);
        return !(f?.dirty && f.errors?.['minlength']);
    }
}
<div class="container-fluid pt-3">
    <div class="card">
        <h5 class="card-header"> login </h5>
        <div class="card-body" [formGroup]="loginForm">
            <div class="form-group">
                <div class="alert alert-danger" *ngIf="IsAuthenticatiionFailed"> Invalid Username or Password</div>
                <label for="UserName"> User Name: </label>
                <input class="form-control" [ngClass]="{'is-valid':loginForm.controls.UserName.valid, 'is-invalid':loginForm.controls.UserName.invalid}" formControlName="UserName" type="text" name="UserName">
                <div class="alert alert-danger" [hidden]="!(loginForm.controls.UserName.dirty && loginForm.controls.UserName.errors?.['required'])"> Enter user name</div>
                <div class="alert alert-danger" [hidden]="!(loginForm.controls.UserName.dirty && loginForm.controls.UserName.errors?.['minlength'])"> Min 5 chars</div>
            </div>
            <div class="form-group">
                <label for="Password"> Password: </label>
                <input class="form-control" [ngClass]="{'is-valid':loginForm.controls.Password.valid, 'is-invalid':loginForm.controls.Password.invalid}" formControlName="Password" type="password" name="Password">
                <div class="alert alert-danger" [hidden]="FailsRequired('Password')"> Enter pass word wanker!!</div>
                <div class="alert alert-danger" [hidden]="FailsMinChar('Password')"> Min 6 chars wanker!!</div>
            </div>
            <input type="button" class="btn btn-success" value="Login" (click)="login()" [disabled]="loginForm.invalid">
        </div>
    </div>
</div>


