import { Component, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Customer } from "../model/customer"
// import * as jwt_decode from "jwt-decode";

@Component({
  selector: 'customer',
  templateUrl: './customer.component.html'
})

export class CustomerComponent {
  Customers:Array<Customer>;
  IsAddNew:boolean;
  FName: string = "";
  LName: string = "";
  // @ViewChild('myForm') CustomerForm: NgForm;
  LoginUser:string="DummyNane"

  constructor() {
    // const decodedToken = jwt_decode(localStorage['token']);
    // this.LoginUser = decodedToken.FName;

    this.IsAddNew = false;

    this.Customers = new Array<Customer>;
    this.Customers.push(new Customer("Sukesh","Marla"));
    this.Customers.push(new Customer("Just","Compile"));
    this.Customers.push(new Customer("Charlie", "Brown"));
  }

  Save(){
    this.Customers.push(new Customer(this.FName, this.LName));
    this.ToggleAdd(false);
    this.reset();
  }

  Cancel(){
    this.ToggleAdd(false);
    this.reset();
  }

  AddNew() {
    this.ToggleAdd(true);
  }

  reset() {
    // this.CustomerForm.resetl();
    this.FName="";
    this.LName="";
  }

  ToggleAdd(hide: boolean) {
    this.IsAddNew = hide;
  }

  logout() {
    localStorage.clear();
  }
}
<nav class="navbar navbar-dark bg-dark justify-content-end">
  <span class="navbar-brand mb-0 h1"> {{LoginUser}}</span>a<a class="navbar-brand mb-0 h1 nav-underline" routerLink='/login' (click)="logout()"><u>Logout</u></a>
</nav>
<div class="container-fluid pt-3">
  <div class="card" [hidden]="IsAddNew">
    <h5 class="card-header">Customer List</h5>
    <div class="card-body">
      <div class="card-title">
        <i class="fas fa-user-plus"></i>
        <input type="button" value="+ Add New" class="btn btn-primary fas fa-coffee fa-xs" (click)="AddNew()">
      </div>
      <table class="table table-striped table-bordered">
        <thead class="thead-dark">
        <tr>
          <th scope="col">First Name</th>
          <th scope="col">Last Name</th>
        </tr>
        </thead>
        <tbody>
          <tr *ngFor="let item of Customers">
            <td scope="row">{{item.FName}}</td>
            <td>{{item.LName}}</td>
          </tr>
        </tbody>
      </table>
    </div>
</div>

<div class="card" [hidden]="!IsAddNew" ngForm #myForm="ngForm">
  <h5 class="card-header">Add new Customer</h5>
  <div class="card-body">
    <div class="form-group">
      <label for="FName">First Name:</label>
      <input class="form-control" [ngClass]="{'is-valid':F.valid, 'is-invalid':F.invalid}" type="text" name="FName" [(ngModel)]="this.FName" required minlength="3" #F="ngModel"><br>
      <div class="alert alert-danger" [hidden]="!(F.dirty && F.errors?.['required'])"> Enter First Name</div>
      <div class="alert alert-danger" [hidden]="!(F.dirty && F.errors?.['minLength'])"> Min 3 chars</div>
    </div>
    <div class="form-group">
      <label for="LName">Last Name:</label>
      <input class="form-control" [ngClass]="{'is-valid':L.valid, 'is-invalid':L.invalid}" type="text" name="LName" [(ngModel)]="this.LName" required minlength="3" #L="ngModel"><br>
      <div class="alert alert-danger" [hidden]="!(F.dirty && F.errors?.['required'])"> Enter Last Name</div>
      <div class="alert alert-danger" [hidden]="!(F.dirty && F.errors?.['minLength'])"> Min 3 chars</div>
    </div>
    <input type="button" value="Save" (click)="Save()" [disabled]="myForm.invalid">   &nbsp;
    <input type="button" value="Cancel" (click)="Cancel()" >
  </div>
</div>
