// Put UI behavior in this instead of template. ie FailsRequired()
import {Component} from '@angular/core';
import {FormGroup, FormBuilder} from '@angular/forms';
import { Customer } from "../model/customer"
@Component({
  selector: 'customer_react',
  templateUrl: './customer_react.component.html'
})

export class Customer_ReactComponent {
  Customers:Array<Customer>;
  IsAddNew:boolean;
  inputForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.inputForm = this.fb.group({
      FName: '',
      LName: ''
    });

    this.IsAddNew = false;
    this.Customers = new Array<Customer>;

    this.Customers.push(new Customer("Bruce","Wayne"));
    this.Customers.push(new Customer("Peter","Parker"));
    this.Customers.push(new Customer("Charlie", "Brown"));
  }

  Save(){
    this.Customers.push(new Customer(this.inputForm.value.FName, this.inputForm.value.LName));
    this.ToggleAdd(false);
    this.reset();
  }

  Cancel(){
    this.ToggleAdd(false);
    this.reset();
  }

  AddNew() {
    this.ToggleAdd(true);
  }

  reset() {
    this.inputForm.reset();
  }

  ToggleAdd(hide: boolean) {
    this.IsAddNew = hide;
  }

  FailsRequired(inputName: string): boolean {
    let f = this.inputForm.get(inputName);
    return !(f?.dirty && f.errors?.['required']);
  }

  FailsMinChar(inputName: string): boolean {
    let f = this.inputForm.get(inputName);
    return !(f?.dirty && f.errors?.['minlength']);
  }
}
<nav class="navbar navbar-dark bg-dark justify-content-end">
    <span class="navbar-brand mb-0 h1"> Welcome Sukesh</span>a<a class="navbar-brand mb-0 h1 nav-underline"
                                                                 href='/login'><u>Logout</u></a>
</nav>
<div class="container-fluid pt-3">
    <div class="card" [hidden]="IsAddNew">
        <h5 class="card-header">Customer List</h5>
        <div class="card-body">
            <div class="card-title">
                <i class="fas fa-user-plus"></i>
                <input type="button" value="+ Add New" class="btn btn-primary fas fa-coffee fa-xs" (click)="AddNew()">
            </div>
            <table class="table table-striped table-bordered">
                <thead class="thead-dark">
                <tr>
                    <th scope="col">First Name</th>
                    <th scope="col">Last Name</th>
                </tr>
                </thead>
                <tbody>
                <tr *ngFor="let item of Customers">
                    <td scope="row">{{item.FName}}</td>
                    <td>{{item.LName}}</td>
                </tr>
                </tbody>
            </table>
        </div>
    </div>

    <div class="card" [hidden]="!IsAddNew" ngForm #myForm="ngForm">
        <h5 class="card-header">Add new Customer</h5>
        <div class="card-body" [formGroup]="inputForm">
            <div class="form-group">
                <label for="FName">First Name:</label>
                <input class="form-control" formControlName="FName" type="text" name="FName">
            </div>
            <div class="form-group">
                <label for="LName">Last Name:</label>
                <input class="form-control" formControlName="LName" type="text" name="LName">
                <div class="alert alert-danger" [hidden]="FailsRequired('LName')"> Enter Last Name word wanker!!</div>
                <div class="alert alert-danger" [hidden]="FailsMinChar('LName')"> Min 3 chars wanker!!</div>
            </div>
            <input type="button" value="Save" (click)="Save()" [disabled]="myForm.invalid"> &nbsp;
            <input type="button" value="Cancel" (click)="Cancel()">
        </div>
    </div>
</div>
